	//Parse the strings and put them into an ArrayList of LatLng objects as the lists of coordinates
	public ArrayList<LatLng> buildCoordList(String inRouteToParse) {		
		//Make sure the route name is valid. Get the coordinates string for the route we want to parse
		String coordinates = routeHashMap.get(inRouteToParse);
		if (coordinates == null) {
			return new ArrayList<LatLng>();
		}
		
		ArrayList<LatLng> routeCoordList = new ArrayList<LatLng>();
			
		//Tokenize the coordinates
		String delims = "[;]+";
		String[] tokens = coordinates.split(delims);
		ArrayList<String> coordArray = new ArrayList<String>(Arrays.asList(tokens));	//ArrayList where each element is a string containing the 3 coordinates
			
		//Now we need to split each coordinate into a 3 object ArrayList
		for (String coord : coordArray) {
			String coordDelims = "[,]+";
			String[] coordTokens = coord.split(coordDelims);
			LatLng latLng = new LatLng(Double.parseDouble(coordTokens[1]), Double.parseDouble(coordTokens[0]));		//Be careful! The lextran coordinate info lists longitude followed by latitude
			routeCoordList.add(latLng);		//Add the arrayList of 3 identifiers to the list of coordinates				
		}
	
		return routeCoordList;
	}
